sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var whatneeded = g_form.getValue('u_what_needed');

	g_form.clearOptions('u_what_needed');
	//if the new value is request type field is HR then
	// add the choice list of What Needed field with the following values
	if (newValue == 'hr'){
		g_form.addOption('u_what_needed','hr1', 'HR 1');
		g_form.addOption('u_what_needed','hr2', 'HR 2');
		g_form.addOption('u_what_needed','hr3','HR 3');
	}

	//if the new value is request type field is Facilities then
	// add the choice list of What Needed field with the following values

	if(newValue == 'facilities'){
		g_form.addOption('u_what_needed','facilities1', 'Facilites 1');
		g_form.addOption('u_what_needed','facilities2', 'Facilites 2');
		g_form.addOption('u_what_needed','facilities3', 'Facilites 3');
	}

	//if the new value is request type field is Legal then
	// add the choice list of What Needed field with the following values
	if (newValue == 'legal'){
		g_form.addOption('u_what_needed','legal1', 'Legal 1');
		g_form.addOption('u_what_needed','legal2', 'Legal 2');
		g_form.addOption('u_what_needed','legal3', 'Legal 3');
	}
   //Type appropriate comment here, and begin script below
   //if the form is loading and the record is new record, set the u_what_needed value to the 
	// value from the record before it was loaded

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}
}
